USE `innovation-web`;

/*Table structure for table `t_article` */

DROP TABLE IF EXISTS `t_article`;

CREATE TABLE `t_article` (
  `id` int(30) NOT NULL AUTO_INCREMENT COMMENT '文章id',
  `title` varchar(250) NOT NULL COMMENT '标题',
  `content` text NOT NULL COMMENT '内容',
  `type_id` int(30) NOT NULL COMMENT '类别',
  `created_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `last_modified_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '最近修改时间',
  `creator_id` int(30) NOT NULL COMMENT '创建者id',
  `read_num` int(30) NOT NULL DEFAULT '0' COMMENT '阅读次数',
  `is_audited` int(5) DEFAULT '0' COMMENT '0 未审核 1 审核通过',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=83 DEFAULT CHARSET=utf8;

/*Data for the table `t_article` */

insert  into `t_article`(`id`,`title`,`content`,`type_id`,`created_time`,`last_modified_time`,`creator_id`,`read_num`,`is_audited`) values (10,'我校教育部哲学社会科学研究重大课题攻关项目','　4月2日，由我校历史文化学院刘厚生教授为首席专家承担的教育部哲学社会科学研究重大课题攻关项目“中国满语文保护抢救口述史与满语音像资料库建构”举行开题报告会。\r\n\r\n　　由北京大学赵杰教授、中国第一历史档案馆吴元丰研究馆员、中央民族大学赵志忠教授、黑龙江大学赵阿平教授、辽宁省民族研究所何晓芳研究员等专家组成的评议专家组，吉林省政府新闻办对外交流处处长李校时，省文化厅非物质文化遗产处处长张志强，我校副校长韩东育教授及社会科学处、历史文化学院的负责人出席了会议。\r\n\r\n　　韩东育代表学校对课题组表示祝贺，对各位专家参与开题评审表示感谢。他首先肯定了刘厚生教授对满语传承与保护所作出的贡献和深厚的满学功底，认为该项目课题对于清史、满族史学、东北边疆史等研究具有重要意义，也契合了国家抢救保护“绝学”的需求。他希望各位与会专家对课题的开展给予深度指导，学校也将积极做好相关保障工作。\r\n\r\n　　刘厚生教授介绍了项目的相关情况。着重强调了当前满语研究保护中急需关注的几大问题，提出了要对满语遗存和应用现状作出全面普查及建构满语音像资料库的两大任务。六位子课题组负责人分别对所承担项目的研究内容、研究方法及预期成果等内容逐一作了汇报。与会专家一致认为,该项目研究对于满语保护、满学学科构建等方面意义重大，并从子课题设计、团队构成等方面肯定了该项目的可操作性。各位专家从资料库建构的相关技术、调研重点、语音资料整理等方面提出了建设性意见。五位评议专家均表示，中国满语文保护抢救工作迫在眉睫，满语研究者应形成合力，为项目的顺利实施提供必要的支持。\r\n\r\n　　至此，我校2016年获得的六项哲学社会科学研究国家级重大项目均成功开题。学校将全面贯彻落实习近平总书记在哲学社会科学工作座谈会上的讲话精神和全国高校思想政治工作会议精神，以学校第十四次党代会和“十三五”发展规划提出的“文科振兴繁荣”为目标，进一步激活广大教师从事重大前沿性课题研究的内生动力，加强顶层设计和团队建设，推进协同创新，力求以重大项目的研究为牵引，产出一系列重大创新性研究成果，更好地推进学校哲学社会科学的繁荣发展。',7,'2017-04-12 15:19:58','2017-04-12 15:19:58',3,0,0),(11,'我校教育部哲学社会科学研究重大课题攻关项目','　4月2日，由我校历史文化学院刘厚生教授为首席专家承担的教育部哲学社会科学研究重大课题攻关项目“中国满语文保护抢救口述史与满语音像资料库建构”举行开题报告会。\r\n\r\n　　由北京大学赵杰教授、中国第一历史档案馆吴元丰研究馆员、中央民族大学赵志忠教授、黑龙江大学赵阿平教授、辽宁省民族研究所何晓芳研究员等专家组成的评议专家组，吉林省政府新闻办对外交流处处长李校时，省文化厅非物质文化遗产处处长张志强，我校副校长韩东育教授及社会科学处、历史文化学院的负责人出席了会议。\r\n\r\n　　韩东育代表学校对课题组表示祝贺，对各位专家参与开题评审表示感谢。他首先肯定了刘厚生教授对满语传承与保护所作出的贡献和深厚的满学功底，认为该项目课题对于清史、满族史学、东北边疆史等研究具有重要意义，也契合了国家抢救保护“绝学”的需求。他希望各位与会专家对课题的开展给予深度指导，学校也将积极做好相关保障工作。\r\n\r\n　　刘厚生教授介绍了项目的相关情况。着重强调了当前满语研究保护中急需关注的几大问题，提出了要对满语遗存和应用现状作出全面普查及建构满语音像资料库的两大任务。六位子课题组负责人分别对所承担项目的研究内容、研究方法及预期成果等内容逐一作了汇报。与会专家一致认为,该项目研究对于满语保护、满学学科构建等方面意义重大，并从子课题设计、团队构成等方面肯定了该项目的可操作性。各位专家从资料库建构的相关技术、调研重点、语音资料整理等方面提出了建设性意见。五位评议专家均表示，中国满语文保护抢救工作迫在眉睫，满语研究者应形成合力，为项目的顺利实施提供必要的支持。\r\n\r\n　　至此，我校2016年获得的六项哲学社会科学研究国家级重大项目均成功开题。学校将全面贯彻落实习近平总书记在哲学社会科学工作座谈会上的讲话精神和全国高校思想政治工作会议精神，以学校第十四次党代会和“十三五”发展规划提出的“文科振兴繁荣”为目标，进一步激活广大教师从事重大前沿性课题研究的内生动力，加强顶层设计和团队建设，推进协同创新，力求以重大项目的研究为牵引，产出一系列重大创新性研究成果，更好地推进学校哲学社会科学的繁荣发展。',8,'2017-04-12 15:20:00','2017-04-12 15:20:00',4,4,0),(12,'我校教育部哲学社会科学研究重大课题攻关项目','　4月2日，由我校历史文化学院刘厚生教授为首席专家承担的教育部哲学社会科学研究重大课题攻关项目“中国满语文保护抢救口述史与满语音像资料库建构”举行开题报告会。\r\n\r\n　　由北京大学赵杰教授、中国第一历史档案馆吴元丰研究馆员、中央民族大学赵志忠教授、黑龙江大学赵阿平教授、辽宁省民族研究所何晓芳研究员等专家组成的评议专家组，吉林省政府新闻办对外交流处处长李校时，省文化厅非物质文化遗产处处长张志强，我校副校长韩东育教授及社会科学处、历史文化学院的负责人出席了会议。\r\n\r\n　　韩东育代表学校对课题组表示祝贺，对各位专家参与开题评审表示感谢。他首先肯定了刘厚生教授对满语传承与保护所作出的贡献和深厚的满学功底，认为该项目课题对于清史、满族史学、东北边疆史等研究具有重要意义，也契合了国家抢救保护“绝学”的需求。他希望各位与会专家对课题的开展给予深度指导，学校也将积极做好相关保障工作。\r\n\r\n　　刘厚生教授介绍了项目的相关情况。着重强调了当前满语研究保护中急需关注的几大问题，提出了要对满语遗存和应用现状作出全面普查及建构满语音像资料库的两大任务。六位子课题组负责人分别对所承担项目的研究内容、研究方法及预期成果等内容逐一作了汇报。与会专家一致认为,该项目研究对于满语保护、满学学科构建等方面意义重大，并从子课题设计、团队构成等方面肯定了该项目的可操作性。各位专家从资料库建构的相关技术、调研重点、语音资料整理等方面提出了建设性意见。五位评议专家均表示，中国满语文保护抢救工作迫在眉睫，满语研究者应形成合力，为项目的顺利实施提供必要的支持。\r\n\r\n　　至此，我校2016年获得的六项哲学社会科学研究国家级重大项目均成功开题。学校将全面贯彻落实习近平总书记在哲学社会科学工作座谈会上的讲话精神和全国高校思想政治工作会议精神，以学校第十四次党代会和“十三五”发展规划提出的“文科振兴繁荣”为目标，进一步激活广大教师从事重大前沿性课题研究的内生动力，加强顶层设计和团队建设，推进协同创新，力求以重大项目的研究为牵引，产出一系列重大创新性研究成果，更好地推进学校哲学社会科学的繁荣发展。',9,'2017-04-12 15:20:02','2017-04-12 15:20:02',4,6,0),(13,'我校教育部哲学社会科学研究重大课题攻关项目“中国满语文保护抢救口述史与满语音像资料库建构”开题报告会举行','　4月2日，由我校历史文化学院刘厚生教授为首席专家承担的教育部哲学社会科学研究重大课题攻关项目“中国满语文保护抢救口述史与满语音像资料库建构”举行开题报告会。\r\n\r\n　　由北京大学赵杰教授、中国第一历史档案馆吴元丰研究馆员、中央民族大学赵志忠教授、黑龙江大学赵阿平教授、辽宁省民族研究所何晓芳研究员等专家组成的评议专家组，吉林省政府新闻办对外交流处处长李校时，省文化厅非物质文化遗产处处长张志强，我校副校长韩东育教授及社会科学处、历史文化学院的负责人出席了会议。\r\n\r\n　　韩东育代表学校对课题组表示祝贺，对各位专家参与开题评审表示感谢。他首先肯定了刘厚生教授对满语传承与保护所作出的贡献和深厚的满学功底，认为该项目课题对于清史、满族史学、东北边疆史等研究具有重要意义，也契合了国家抢救保护“绝学”的需求。他希望各位与会专家对课题的开展给予深度指导，学校也将积极做好相关保障工作。\r\n\r\n　　刘厚生教授介绍了项目的相关情况。着重强调了当前满语研究保护中急需关注的几大问题，提出了要对满语遗存和应用现状作出全面普查及建构满语音像资料库的两大任务。六位子课题组负责人分别对所承担项目的研究内容、研究方法及预期成果等内容逐一作了汇报。与会专家一致认为,该项目研究对于满语保护、满学学科构建等方面意义重大，并从子课题设计、团队构成等方面肯定了该项目的可操作性。各位专家从资料库建构的相关技术、调研重点、语音资料整理等方面提出了建设性意见。五位评议专家均表示，中国满语文保护抢救工作迫在眉睫，满语研究者应形成合力，为项目的顺利实施提供必要的支持。\r\n\r\n　　至此，我校2016年获得的六项哲学社会科学研究国家级重大项目均成功开题。学校将全面贯彻落实习近平总书记在哲学社会科学工作座谈会上的讲话精神和全国高校思想政治工作会议精神，以学校第十四次党代会和“十三五”发展规划提出的“文科振兴繁荣”为目标，进一步激活广大教师从事重大前沿性课题研究的内生动力，加强顶层设计和团队建设，推进协同创新，力求以重大项目的研究为牵引，产出一系列重大创新性研究成果，更好地推进学校哲学社会科学的繁荣发展。',10,'2017-04-12 15:20:05','2017-04-12 15:20:05',5,1,0),(14,'我校“东北抗联与伪满洲国资料研究中心”揭牌仪式举行','4月6日，我校“东北抗联与伪满洲国资料研究中心”揭牌仪式及学术研讨会在历史文化学院举行。中国社会科学院近代史研究所副所长金以林研究员，中心合作基地磐石市市委常委、宣传部部长张峰，我校党委副书记王延、副校长韩东育，学校发展规划处、社会科学处、历史文化学院及磐石市旅游局等单位相关负责人，以及来自中国社会科学院、南京大学、香港中文大学、上海交通大学、南京师范大学、吉林大学等高校和科研机构的专家学者参加了揭牌仪式。\r\n\r\n　　研讨会上，韩东育教授，张峰，南京大学历史学院陈谦平教授，中国社会科学院近代史研究所研究员、《抗日战争研究》主编高士华，吉林省社科院副院长刘信君研究员，伪满皇宫博物院院长王志强，我校历史文化学院刘景岚教授先后作主题发言。与会代表还围绕整合研究力量、构建研究体系、提高研究水准等问题交流了看法。\r\n\r\n　　“东北抗联与伪满洲国资料研究中心”是依托我校历史文化学院成立的研究机构，旨在通过资料的收集与整理，打造东北抗联史和伪满洲国史研究的新平台。中心成立之前，我校历史文化学院的师生已经做了大量的档案资料、口述资料和国内外日文资料的搜集工作，并与磐石市、桦甸市等县市档案机构合作，发掘、整理了一批抗联史资料。中心的成立，对于推动东北抗联史、伪满洲国史乃至抗日战争史的深入研究具有重要意义。',9,'2017-04-12 15:20:29','2017-04-12 15:20:29',4,1,0),(15,'校工会召开会议部署第五届“双代会”筹备工作','　按照工作日程安排，我校第五届“双代会”将于6月13日至16日召开。4月7日，校工会作为“双代会”工作机构，组织召开了第四届“双代会”执委会委员、教代会代表组长及部门工会主席工作会议，部署第五届“双代会”筹备工作。\r\n\r\n　　会上，宣读了学校党委“关于同意召开东北师范大学第五届‘双代会’的批复”。第五届“双代会”筹备工作领导小组副组长、校工会负责人作了筹备工作汇报，对会议召开的具体时间、指导思想、主要议题、具体筹备日程安排、会议代表组成等情况作了详细介绍。筹备工作领导小组相关人员对《东北师范大学第五届“双代会”代表和执委会委员候选人选举办法》《东北师范大学第五届教职工暨工会会员代表大会代表提案工作安排意见》两份文件作了解读和说明。为进一步普及“双代会”知识，增强广大教职工的民主政治意识和“双代会”代表的履职能力，会议还下发了《“双代会”基础知识问答》手册。\r\n\r\n　　学校对第五届“双代会”的筹备工作非常重视。3月下旬，校工会在学校党委常委会上作了专项工作汇报，党政主要领导都对相关工作提出了明确要求。负责相关筹备工作的校领导也相继组织召开了协调会，部署任务的落实。\r\n\r\n　　根据本次会议部署，第五届“双代会”代表和执委会委员候选人选举工作正式启动。\r\n\r\n供稿单位：校工会撰稿：白立新网络编辑：谭海洪\r\n',1,'2017-04-12 15:22:47','2017-04-12 15:22:47',5,10,0),(16,'英国剑桥大学亚述学家麦克马洪教授来校访问交流','<p style=\"margin: 0px 0px 11px; padding: 0px; font-style: normal; font-variant: normal; font-weight: normal; font-stretch: normal; font-size: 15px; line-height: 1.7; font-family: \'Microsoft YaHei\'; color: #333333; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">　3月27日至4月5日，英国剑桥大学亚述学家奥古斯塔&middot;麦克马洪（Augusta McMahon）教授应邀到我校世界古典文明史研究所进行学术访问交流。</p>\r\n<p style=\"margin: 0px 0px 11px; padding: 0px; font-style: normal; font-variant: normal; font-weight: normal; font-stretch: normal; font-size: 15px; line-height: 1.7; font-family: \'Microsoft YaHei\'; color: #333333; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">　　访问期间，麦克马洪教授作了三场学术报告，较为全面地介绍了自己对两河流域考古、两河流域早期城镇化以及战争与社会冲突的最新研究成果。其中，在题为&ldquo;公元前三千纪两河流域南部的牧群管理&rdquo;的报告中，她结合公元前三千纪两河流域南部人类、动物和环境之间的关系，通过考古挖掘所发现的骨头和牙齿中的同位素，分析了当时饲养动物的主要食物和动物饮用水情况，初步研究表明，当时对牛等动物采取了不同的放牧方式，可能不存在专门为王室随葬而畜养的牧群，这为研究乌尔王室墓葬等问题提供了新的思路。</p>\r\n<p style=\"margin: 0px 0px 11px; padding: 0px; font-style: normal; font-variant: normal; font-weight: normal; font-stretch: normal; font-size: 15px; line-height: 1.7; font-family: \'Microsoft YaHei\'; color: #333333; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">　　交流中，麦克马洪教授还与世界古典文明史研究所教师就亚述学未来学科建设、学术研究以及《世界古典文明史杂志》（JAC）的编辑出版进行了有益探讨，并针对我校亚述学学科的研究方法和学科体系的完善提出了建议。</p>\r\n<p style=\"margin: 0px 0px 11px; padding: 0px; font-style: normal; font-variant: normal; font-weight: normal; font-stretch: normal; font-size: 15px; line-height: 1.7; font-family: \'Microsoft YaHei\'; color: #333333; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">　　麦克马洪教授的主要研究领域为古代两河流域城市考古，她长期参与叙利亚北部布拉克遗址（Tell Brak）的考古挖掘工作，代表著作为《遗失的遗产：伊拉克地区博物馆被盗文物》。</p>\r\n<p style=\"margin: 0px 0px 11px; padding: 0px; font-style: normal; font-variant: normal; font-weight: normal; font-stretch: normal; font-size: 15px; line-height: 1.7; font-family: \'Microsoft YaHei\'; color: #333333; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"margin: 0px 20px 0px 0px; padding: 0px; display: inline; color: #222222; font-family: 微软雅黑, \'Microsoft YaHei\', Verdana, Arial, Helvetica, sans-serif; font-size: 15px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 30px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">供稿单位：<em style=\"margin: 0px; padding: 0px; font-style: normal;\">世界古典文明史研究所</em></span><span style=\"margin: 0px 20px 0px 0px; padding: 0px; display: inline; color: #222222; font-family: 微软雅黑, \'Microsoft YaHei\', Verdana, Arial, Helvetica, sans-serif; font-size: 15px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 30px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">撰稿：<em style=\"margin: 0px; padding: 0px; font-style: normal;\">张强、刘军、王光胜</em></span><span style=\"margin: 0px 20px 0px 0px; padding: 0px; display: inline; color: #222222; font-family: 微软雅黑, \'Microsoft YaHei\', Verdana, Arial, Helvetica, sans-serif; font-size: 15px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 30px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">网络编辑：<em style=\"margin: 0px; padding: 0px; font-style: normal;\">谭海洪</em></span></p>',11,'2017-04-15 19:54:16','2017-04-15 19:54:16',1,2,0),(17,'外国语学院“东师外语致知讲坛”开讲啦','<div class=\"read\" style=\"margin: 0px; padding: 0px; line-height: 1.7; font-size: 15px; color: #333333; font-family: \'Microsoft YaHei\' !important; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\">\r\n<p style=\"margin: 0px 0px 11px; padding: 0px; font-style: normal; font-variant: normal; font-weight: normal; font-stretch: normal; font-size: 15px; line-height: 1.7; font-family: \'Microsoft YaHei\' !important; color: #333333;\">　　近日，外国语学院举行&ldquo;东师外语致知讲坛&rdquo;启动仪式并举办首场讲座。&nbsp;&nbsp;&nbsp;</p>\r\n<p style=\"margin: 0px 0px 11px; padding: 0px; font-style: normal; font-variant: normal; font-weight: normal; font-stretch: normal; font-size: 15px; line-height: 1.7; font-family: \'Microsoft YaHei\' !important; color: #333333;\">　　启动仪式上，杨忠教授、张绍杰教授、林正军教授分别致辞，杨忠教授为学院赠送了&ldquo;东师外语致知讲坛&rdquo;的亲笔题字。</p>\r\n<p style=\"margin: 0px 0px 11px; padding: 0px; font-style: normal; font-variant: normal; font-weight: normal; font-stretch: normal; font-size: 15px; line-height: 1.7; font-family: \'Microsoft YaHei\' !important; color: #333333;\">　　随后，刘永兵教授为师生作了题为&ldquo;外语教育研究的选题与研究方法&rdquo;的学术讲座。他从应用语言学初级研究者遇到的问题、实证研究的基本原则、前沿课题、文献综述如何撰写、理论和研究框架如何建构以及学术论文写作的步骤等方面对外语教育研究进行了详细讲解。</p>\r\n<p style=\"margin: 0px 0px 11px; padding: 0px; font-style: normal; font-variant: normal; font-weight: normal; font-stretch: normal; font-size: 15px; line-height: 1.7; font-family: \'Microsoft YaHei\' !important; color: #333333;\">　　&ldquo;东师外语致知讲坛&rdquo; 将定期邀请国内外知名学者来校进行学术讲座，搭建学术交流的平台，进一步促进学院的学科建设、教师发展及人才培养。</p>\r\n<p style=\"margin: 0px 0px 11px; padding: 0px; font-style: normal; font-variant: normal; font-weight: normal; font-stretch: normal; font-size: 15px; line-height: 1.7; font-family: \'Microsoft YaHei\' !important; color: #333333;\">&nbsp;&nbsp;</p>\r\n</div>\r\n<div class=\"entry_meta\" style=\"margin: 0px 0px 16px; padding: 5px 0px; line-height: 19px; font-size: 15px; color: #222222; text-indent: 2em; font-family: 微软雅黑, \'Microsoft YaHei\', Verdana, Arial, Helvetica, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"margin: 0px 20px 0px 0px; padding: 0px; display: inline;\">供稿单位：<em style=\"margin: 0px; padding: 0px; font-style: normal;\">外国语学院</em></span><span style=\"margin: 0px 20px 0px 0px; padding: 0px; display: inline;\">撰稿：<em style=\"margin: 0px; padding: 0px; font-style: normal;\">王香丹</em></span><span style=\"margin: 0px 20px 0px 0px; padding: 0px; display: inline;\">网络编辑：<em style=\"margin: 0px; padding: 0px; font-style: normal;\">谭海洪</em></span></div>',1,'2017-04-15 20:00:17','2017-04-15 20:00:17',1,6,0),(18,'互联网3','<pre id=\"best-content-1509217389\" class=\"best-text mb-10\" style=\"margin: 10px 0px; padding: 0px; font-family: \'PingFang SC\', \'Lantinghei SC\', \'Microsoft YaHei\', arial, 宋体, sans-serif, tahoma; white-space: pre-wrap; word-wrap: break-word; font-size: 16px; line-height: 29px; color: #333333; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial; min-height: 55px;\">我与父亲不相见已二年余了，我最不能忘记的是他的背影。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />那年冬天，祖母死了，父亲的差使也交卸了，正是祸不单行的日子。我从北京到徐州，打算跟着父亲奔丧回家。到徐州见着父亲，看见满院狼藉的东西，又想起祖母，不禁簌簌地流下眼泪。父亲说：&ldquo;事已如此，不必难过，好在天无绝人之路！&rdquo; <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />回家<a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E5%8F%98%E5%8D%96%E5%85%B8%E8%B4%A8&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">变卖典质</a>，父亲还了亏空；又借钱办了丧事。这些日子，家中光景很是惨淡，一半为了丧事，一半为了父亲赋闲。丧事完毕，父亲要到南京谋事，我也要回北京念书，我们便同行。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />到南京时，有朋友约去游逛，勾留了一日；第二日上午便须渡江到浦口，下午上车北去。父亲因为事忙，本已说定不送我，叫旅馆里一个熟识的茶房陪我同去。他再三嘱咐茶房，甚是仔细。但他终于不放心，怕茶房不妥帖；颇踌躇了一会。其实我那年已二十岁，北京已来往过两三次，是没有什么要紧的了。他踌躇了一会，终于决定还是自己送我去。我两三劝他不必去；他只说，&ldquo;不要紧，他们去不好！&rdquo; <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />我们过了江，进了车站。我买票，他忙着照看行李。行李太多了，得向脚夫行些小费才可过去。他便又忙着和他们讲价钱。我那时真是聪明过分，总觉他说话不大漂亮，非自己插嘴不可，但他终于讲定了价钱；就送我上车。他给我拣定了靠车门的一张椅子；我将他给我做的紫毛大衣铺好坐位。他嘱我路上小心，夜里警醒些，不要受凉。又嘱托茶房好好照应我。我心里暗笑他的迂；他们只认得钱，托他们只是白托！而且我这样大年纪的人，难道还不能料理自己么？唉，我现在想想，那时真是太聪明了！ <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />我说道，&ldquo;爸爸，你走吧。&rdquo;他望车外看了看说：&ldquo;我买几个橘子去。你就在此地，不要走动。&rdquo;我看那边月台的栅栏外有几个卖东西的等着顾客。走到那边月台，须穿过铁道，须跳下去又爬上去。父亲是一个胖子，走过去自然要费事些。我本来要去的，他不肯，只好让他去。我看见他戴着黑布小帽，穿着黑布大马褂，深青布棉袍，蹒跚地走到铁道边，慢慢探身下去，尚不大难。可是他穿过铁道，要爬上那边月台，就不容易了。他用两手攀着上面，两脚再向上缩；他肥胖的身子向左微倾，显出努力的样子。这时我看见他的背影，我的泪很快地流下来了。我赶紧拭干了泪。怕他看见，也怕别人看见。我再向外看时，他已抱了朱红的橘子往回走了。过铁道时，他先将橘子散放在地上，自己慢慢爬下，再抱起橘子走。到这边时，我赶紧去搀他。他和我走到车上，将橘子一股脑儿放在我的皮大衣上。于是扑扑衣上的泥土，心里很轻松似的。过一会说：&ldquo;我走了，到那边来信！&rdquo;我望着他走出去。他走了几步，回过头看见我，说：&ldquo;进去吧，里边没人。&rdquo;等他的背影混入来来往往的人里，再找不着了，我便进来坐下，我的眼泪又来了。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />近几年来，父亲和我都是东奔西走，家中光景是一日不如一日。他少年出外谋生，独力支持，做了许多大事。哪知老境却如此颓唐！他触目伤怀，自然情不能自已。情郁于中，自然要发之于外；家庭琐屑便往往触他之怒。他待我渐渐不同往日。但最近两年的不见，他终于忘却我的不好，只是惦记着我，惦记着我的儿子。我北来后，他写了一信给我，信中说道：&ldquo;我身体平安，惟膀子疼痛厉害，<a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E4%B8%BE%E7%AE%B8%E6%8F%90%E7%AC%94&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">举箸提笔</a>，诸多不便，大约大去之期不远矣。&rdquo;我读到此处，在晶莹的泪光中，又看见那肥胖的、青布棉袍黑布马褂的背影。唉！我不知何时再能与他相见！ </pre>',1,'2017-04-16 16:27:34','2017-04-16 16:27:34',1,0,0),(19,'互联网4','<pre id=\"best-content-1509217389\" class=\"best-text mb-10\" style=\"margin: 10px 0px; padding: 0px; font-family: \'PingFang SC\', \'Lantinghei SC\', \'Microsoft YaHei\', arial, 宋体, sans-serif, tahoma; white-space: pre-wrap; word-wrap: break-word; font-size: 16px; line-height: 29px; color: #333333; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial; min-height: 55px;\">我与父亲不相见已二年余了，我最不能忘记的是他的背影。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />那年冬天，祖母死了，父亲的差使也交卸了，正是祸不单行的日子。我从北京到徐州，打算跟着父亲奔丧回家。到徐州见着父亲，看见满院狼藉的东西，又想起祖母，不禁簌簌地流下眼泪。父亲说：&ldquo;事已如此，不必难过，好在天无绝人之路！&rdquo; <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />回家<a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E5%8F%98%E5%8D%96%E5%85%B8%E8%B4%A8&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">变卖典质</a>，父亲还了亏空；又借钱办了丧事。这些日子，家中光景很是惨淡，一半为了丧事，一半为了父亲赋闲。丧事完毕，父亲要到南京谋事，我也要回北京念书，我们便同行。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />到南京时，有朋友约去游逛，勾留了一日；第二日上午便须渡江到浦口，下午上车北去。父亲因为事忙，本已说定不送我，叫旅馆里一个熟识的茶房陪我同去。他再三嘱咐茶房，甚是仔细。但他终于不放心，怕茶房不妥帖；颇踌躇了一会。其实我那年已二十岁，北京已来往过两三次，是没有什么要紧的了。他踌躇了一会，终于决定还是自己送我去。我两三劝他不必去；他只说，&ldquo;不要紧，他们去不好！&rdquo; <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />我们过了江，进了车站。我买票，他忙着照看行李。行李太多了，得向脚夫行些小费才可过去。他便又忙着和他们讲价钱。我那时真是聪明过分，总觉他说话不大漂亮，非自己插嘴不可，但他终于讲定了价钱；就送我上车。他给我拣定了靠车门的一张椅子；我将他给我做的紫毛大衣铺好坐位。他嘱我路上小心，夜里警醒些，不要受凉。又嘱托茶房好好照应我。我心里暗笑他的迂；他们只认得钱，托他们只是白托！而且我这样大年纪的人，难道还不能料理自己么？唉，我现在想想，那时真是太聪明了！ <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />我说道，&ldquo;爸爸，你走吧。&rdquo;他望车外看了看说：&ldquo;我买几个橘子去。你就在此地，不要走动。&rdquo;我看那边月台的栅栏外有几个卖东西的等着顾客。走到那边月台，须穿过铁道，须跳下去又爬上去。父亲是一个胖子，走过去自然要费事些。我本来要去的，他不肯，只好让他去。我看见他戴着黑布小帽，穿着黑布大马褂，深青布棉袍，蹒跚地走到铁道边，慢慢探身下去，尚不大难。可是他穿过铁道，要爬上那边月台，就不容易了。他用两手攀着上面，两脚再向上缩；他肥胖的身子向左微倾，显出努力的样子。这时我看见他的背影，我的泪很快地流下来了。我赶紧拭干了泪。怕他看见，也怕别人看见。我再向外看时，他已抱了朱红的橘子往回走了。过铁道时，他先将橘子散放在地上，自己慢慢爬下，再抱起橘子走。到这边时，我赶紧去搀他。他和我走到车上，将橘子一股脑儿放在我的皮大衣上。于是扑扑衣上的泥土，心里很轻松似的。过一会说：&ldquo;我走了，到那边来信！&rdquo;我望着他走出去。他走了几步，回过头看见我，说：&ldquo;进去吧，里边没人。&rdquo;等他的背影混入来来往往的人里，再找不着了，我便进来坐下，我的眼泪又来了。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />近几年来，父亲和我都是东奔西走，家中光景是一日不如一日。他少年出外谋生，独力支持，做了许多大事。哪知老境却如此颓唐！他触目伤怀，自然情不能自已。情郁于中，自然要发之于外；家庭琐屑便往往触他之怒。他待我渐渐不同往日。但最近两年的不见，他终于忘却我的不好，只是惦记着我，惦记着我的儿子。我北来后，他写了一信给我，信中说道：&ldquo;我身体平安，惟膀子疼痛厉害，<a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E4%B8%BE%E7%AE%B8%E6%8F%90%E7%AC%94&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">举箸提笔</a>，诸多不便，大约大去之期不远矣。&rdquo;我读到此处，在晶莹的泪光中，又看见那肥胖的、青布棉袍黑布马褂的背影。唉！我不知何时再能与他相见！ </pre>',1,'2017-04-16 16:27:49','2017-04-16 16:27:49',1,0,0),(20,'互联网5','<pre id=\"best-content-1509217389\" class=\"best-text mb-10\" style=\"margin: 10px 0px; padding: 0px; font-family: \'PingFang SC\', \'Lantinghei SC\', \'Microsoft YaHei\', arial, 宋体, sans-serif, tahoma; white-space: pre-wrap; word-wrap: break-word; font-size: 16px; line-height: 29px; color: #333333; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial; min-height: 55px;\">我与父亲不相见已二年余了，我最不能忘记的是他的背影。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />那年冬天，祖母死了，父亲的差使也交卸了，正是祸不单行的日子。我从北京到徐州，打算跟着父亲奔丧回家。到徐州见着父亲，看见满院狼藉的东西，又想起祖母，不禁簌簌地流下眼泪。父亲说：&ldquo;事已如此，不必难过，好在天无绝人之路！&rdquo; <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />回家<a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E5%8F%98%E5%8D%96%E5%85%B8%E8%B4%A8&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">变卖典质</a>，父亲还了亏空；又借钱办了丧事。这些日子，家中光景很是惨淡，一半为了丧事，一半为了父亲赋闲。丧事完毕，父亲要到南京谋事，我也要回北京念书，我们便同行。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />到南京时，有朋友约去游逛，勾留了一日；第二日上午便须渡江到浦口，下午上车北去。父亲因为事忙，本已说定不送我，叫旅馆里一个熟识的茶房陪我同去。他再三嘱咐茶房，甚是仔细。但他终于不放心，怕茶房不妥帖；颇踌躇了一会。其实我那年已二十岁，北京已来往过两三次，是没有什么要紧的了。他踌躇了一会，终于决定还是自己送我去。我两三劝他不必去；他只说，&ldquo;不要紧，他们去不好！&rdquo; <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />我们过了江，进了车站。我买票，他忙着照看行李。行李太多了，得向脚夫行些小费才可过去。他便又忙着和他们讲价钱。我那时真是聪明过分，总觉他说话不大漂亮，非自己插嘴不可，但他终于讲定了价钱；就送我上车。他给我拣定了靠车门的一张椅子；我将他给我做的紫毛大衣铺好坐位。他嘱我路上小心，夜里警醒些，不要受凉。又嘱托茶房好好照应我。我心里暗笑他的迂；他们只认得钱，托他们只是白托！而且我这样大年纪的人，难道还不能料理自己么？唉，我现在想想，那时真是太聪明了！ <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />我说道，&ldquo;爸爸，你走吧。&rdquo;他望车外看了看说：&ldquo;我买几个橘子去。你就在此地，不要走动。&rdquo;我看那边月台的栅栏外有几个卖东西的等着顾客。走到那边月台，须穿过铁道，须跳下去又爬上去。父亲是一个胖子，走过去自然要费事些。我本来要去的，他不肯，只好让他去。我看见他戴着黑布小帽，穿着黑布大马褂，深青布棉袍，蹒跚地走到铁道边，慢慢探身下去，尚不大难。可是他穿过铁道，要爬上那边月台，就不容易了。他用两手攀着上面，两脚再向上缩；他肥胖的身子向左微倾，显出努力的样子。这时我看见他的背影，我的泪很快地流下来了。我赶紧拭干了泪。怕他看见，也怕别人看见。我再向外看时，他已抱了朱红的橘子往回走了。过铁道时，他先将橘子散放在地上，自己慢慢爬下，再抱起橘子走。到这边时，我赶紧去搀他。他和我走到车上，将橘子一股脑儿放在我的皮大衣上。于是扑扑衣上的泥土，心里很轻松似的。过一会说：&ldquo;我走了，到那边来信！&rdquo;我望着他走出去。他走了几步，回过头看见我，说：&ldquo;进去吧，里边没人。&rdquo;等他的背影混入来来往往的人里，再找不着了，我便进来坐下，我的眼泪又来了。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />近几年来，父亲和我都是东奔西走，家中光景是一日不如一日。他少年出外谋生，独力支持，做了许多大事。哪知老境却如此颓唐！他触目伤怀，自然情不能自已。情郁于中，自然要发之于外；家庭琐屑便往往触他之怒。他待我渐渐不同往日。但最近两年的不见，他终于忘却我的不好，只是惦记着我，惦记着我的儿子。我北来后，他写了一信给我，信中说道：&ldquo;我身体平安，惟膀子疼痛厉害，<a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E4%B8%BE%E7%AE%B8%E6%8F%90%E7%AC%94&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">举箸提笔</a>，诸多不便，大约大去之期不远矣。&rdquo;我读到此处，在晶莹的泪光中，又看见那肥胖的、青布棉袍黑布马褂的背影。唉！我不知何时再能与他相见！ </pre>',1,'2017-04-16 16:28:03','2017-04-16 16:28:03',1,0,0),(21,'互联网6','<pre id=\"best-content-1509217389\" class=\"best-text mb-10\" style=\"margin: 10px 0px; padding: 0px; font-family: \'PingFang SC\', \'Lantinghei SC\', \'Microsoft YaHei\', arial, 宋体, sans-serif, tahoma; white-space: pre-wrap; word-wrap: break-word; font-size: 16px; line-height: 29px; color: #333333; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial; min-height: 55px;\">我与父亲不相见已二年余了，我最不能忘记的是他的背影。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />那年冬天，祖母死了，父亲的差使也交卸了，正是祸不单行的日子。我从北京到徐州，打算跟着父亲奔丧回家。到徐州见着父亲，看见满院狼藉的东西，又想起祖母，不禁簌簌地流下眼泪。父亲说：&ldquo;事已如此，不必难过，好在天无绝人之路！&rdquo; <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />回家<a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E5%8F%98%E5%8D%96%E5%85%B8%E8%B4%A8&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">变卖典质</a>，父亲还了亏空；又借钱办了丧事。这些日子，家中光景很是惨淡，一半为了丧事，一半为了父亲赋闲。丧事完毕，父亲要到南京谋事，我也要回北京念书，我们便同行。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />到南京时，有朋友约去游逛，勾留了一日；第二日上午便须渡江到浦口，下午上车北去。父亲因为事忙，本已说定不送我，叫旅馆里一个熟识的茶房陪我同去。他再三嘱咐茶房，甚是仔细。但他终于不放心，怕茶房不妥帖；颇踌躇了一会。其实我那年已二十岁，北京已来往过两三次，是没有什么要紧的了。他踌躇了一会，终于决定还是自己送我去。我两三劝他不必去；他只说，&ldquo;不要紧，他们去不好！&rdquo; <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />我们过了江，进了车站。我买票，他忙着照看行李。行李太多了，得向脚夫行些小费才可过去。他便又忙着和他们讲价钱。我那时真是聪明过分，总觉他说话不大漂亮，非自己插嘴不可，但他终于讲定了价钱；就送我上车。他给我拣定了靠车门的一张椅子；我将他给我做的紫毛大衣铺好坐位。他嘱我路上小心，夜里警醒些，不要受凉。又嘱托茶房好好照应我。我心里暗笑他的迂；他们只认得钱，托他们只是白托！而且我这样大年纪的人，难道还不能料理自己么？唉，我现在想想，那时真是太聪明了！ <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />我说道，&ldquo;爸爸，你走吧。&rdquo;他望车外看了看说：&ldquo;我买几个橘子去。你就在此地，不要走动。&rdquo;我看那边月台的栅栏外有几个卖东西的等着顾客。走到那边月台，须穿过铁道，须跳下去又爬上去。父亲是一个胖子，走过去自然要费事些。我本来要去的，他不肯，只好让他去。我看见他戴着黑布小帽，穿着黑布大马褂，深青布棉袍，蹒跚地走到铁道边，慢慢探身下去，尚不大难。可是他穿过铁道，要爬上那边月台，就不容易了。他用两手攀着上面，两脚再向上缩；他肥胖的身子向左微倾，显出努力的样子。这时我看见他的背影，我的泪很快地流下来了。我赶紧拭干了泪。怕他看见，也怕别人看见。我再向外看时，他已抱了朱红的橘子往回走了。过铁道时，他先将橘子散放在地上，自己慢慢爬下，再抱起橘子走。到这边时，我赶紧去搀他。他和我走到车上，将橘子一股脑儿放在我的皮大衣上。于是扑扑衣上的泥土，心里很轻松似的。过一会说：&ldquo;我走了，到那边来信！&rdquo;我望着他走出去。他走了几步，回过头看见我，说：&ldquo;进去吧，里边没人。&rdquo;等他的背影混入来来往往的人里，再找不着了，我便进来坐下，我的眼泪又来了。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />近几年来，父亲和我都是东奔西走，家中光景是一日不如一日。他少年出外谋生，独力支持，做了许多大事。哪知老境却如此颓唐！他触目伤怀，自然情不能自已。情郁于中，自然要发之于外；家庭琐屑便往往触他之怒。他待我渐渐不同往日。但最近两年的不见，他终于忘却我的不好，只是惦记着我，惦记着我的儿子。我北来后，他写了一信给我，信中说道：&ldquo;我身体平安，惟膀子疼痛厉害，<a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E4%B8%BE%E7%AE%B8%E6%8F%90%E7%AC%94&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">举箸提笔</a>，诸多不便，大约大去之期不远矣。&rdquo;我读到此处，在晶莹的泪光中，又看见那肥胖的、青布棉袍黑布马褂的背影。唉！我不知何时再能与他相见！ </pre>',1,'2017-04-16 16:28:22','2017-04-16 16:28:22',1,39,0),(22,'创青春全国大学生创业大赛','<pre id=\"best-content-1509217389\" class=\"best-text mb-10\" style=\"margin: 10px 0px; padding: 0px; font-family: \'PingFang SC\', \'Lantinghei SC\', \'Microsoft YaHei\', arial, 宋体, sans-serif, tahoma; white-space: pre-wrap; word-wrap: break-word; font-size: 16px; line-height: 29px; color: #333333; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial; min-height: 55px;\">这几天心里颇不宁静。今晚在院子里坐着乘凉，忽然想起日日走过的荷塘，在这满月的光里 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />，总该另有一番样子吧。月亮渐渐地升高了，墙外马路上孩子们的欢笑，已经听不见了；妻 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />在屋里拍着闰儿，迷迷糊糊地哼着眠歌。我悄悄地披了大衫，带上门出去。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" /><br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />沿着荷塘，是一条曲折的小煤屑路。这是一条幽僻的路；白天也少人走，夜晚更加寂寞。荷 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />塘四面，长着许多树，<a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E8%93%8A%E8%93%8A%E9%83%81%E9%83%81&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">蓊蓊郁郁</a>的。路的一旁，是些杨柳，和一些不知道名字的树。没有月 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />光的晚上，这路上阴森森的，有些怕人。今晚却很好，虽然月光也还是淡淡的。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" /><br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />路上只我一个人，背着手踱着。这一片天地好像是我的；我也像超出了平常的自己，到了另 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />一个世界里。我爱热闹，也爱冷静；爱群居，也爱独处。像今晚上，一个人在这苍茫的月下 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />，什么都可以想，什么都可以不想，便觉是个自由的人。白天里一定要做的事，一定要说的 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />话，现在都可不理。这是独处的妙处；我且受用这无边的荷香月色好了。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" /><br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />曲曲折折的荷塘上面，弥望的是田田的叶子。叶子出水很高，像亭亭的舞女的裙。层层的叶 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />子中间，零星地点缀着些白花，有袅娜地开着，有羞涩的打着朵儿的；正如一粒粒的明珠， <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />又如碧天里的星星，又如刚出浴的美人。微风过处，送来缕缕清香，仿佛远处高楼上渺茫的 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />歌声似的。这时候叶子与花也有一些的颤动，像闪电般，霎时传过荷塘的那边去了。叶子本 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />是肩并肩密密的挨着，这便宛然有了一道凝碧的波痕。叶子底下是脉脉的流水，遮住了，不 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />能见一些颜色；而叶子却更见风致了。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" /><br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />月光如流水一般，静静地泻在这一片叶子和花上。薄薄的青雾浮起在荷塘里。叶子和花仿佛 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />在牛乳中洗过一样；又像笼着轻纱的梦。虽然是满月，天上却有一层淡淡的云，所以不能朗 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />照；但我以为这恰是到了好处－－酣眠固不可少，小睡也别有风味的。月光是隔了树照过来 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />的，高处丛生的灌木，落下参差的斑驳的黑影，却又像是画在荷叶上。塘中的月色并不均匀 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />，但光与影有着和谐的旋律，如梵婀玲上奏着的名曲。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" /><br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />荷塘的四面，远远近近，高高低低的都是树，而杨柳最多。这些树将一片荷塘重重围住；只 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />在小路一旁，漏着几段空隙，像是特为月光留下的。树色一例是阴阴的，乍看像一团烟雾； <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />但杨柳的丰姿，便在烟雾里也辨得出。树梢上隐隐约约的是一带远山，只有些大意罢了。树 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />缝里也漏着一两点路灯光，没精打彩的，是渴睡人的眼。这时候最热闹的，要数树上的蝉声 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />与水里的蛙声；但热闹的是它们的，我什么也没有。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" /><br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />忽然想起采莲的事情来了。采莲是江南的旧俗，似乎很早就有，而六朝时为盛，从诗歌里可 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />以约略知道。采莲的是少年的女子，她们是荡着小船，唱着艳歌去的。采莲人不用说很多， <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />还有看采莲的人。那是一个热闹的季节，也是一个风流的季节。<a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E6%A2%81%E5%85%83%E5%B8%9D&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">梁元帝</a><a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E3%80%8A%E9%87%87%E8%8E%B2%E8%B5%8B%E3%80%8B&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">《采莲赋》</a>里说得好 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />：于是妖童媛女，荡舟心话：[益鸟]首徐回，兼传羽杯；棹将移而藻挂，船欲动而萍开。尔其 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />纤腰束素，迁延顾步；夏始春余，叶嫩花初，恐沾裳而浅笑，畏倾船而敛裾。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" /><br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />可见当时嬉游的光景了。这真是有趣的事，可惜我们现在早已无福消受了。于是又记起<a class=\"baidu-highlight\" style=\"color: #3f88bf; text-decoration: none;\" href=\"https://www.baidu.com/s?wd=%E3%80%8A%E8%A5%BF&amp;tn=44039180_cpr&amp;fenlei=mv6quAkxTZn0IZRqIHckPjm4nH00T1Yvujf4n1fdnHT1mhNhujmd0ZwV5Hcvrjm3rH6sPfKWUMw85HfYnjn4nH6sgvPsT6KdThsqpZwYTjCEQLGCpyw9Uz4Bmy-bIi4WUvYETgN-TLwGUv3EnHbzPj0drHf3PWDdPHTzP1DsPs\" target=\"_blank\" rel=\"nofollow noopener noreferrer\">《西</a> <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />洲曲》里的句子： <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" /><br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />采莲南塘秋，莲花过人头；低头弄莲子，莲子清如水。 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" /><br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />今晚若有采莲人，这儿的莲花也算得&ldquo;过人头&rdquo;了；只不见一些流水的影子，是不行的。这 <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />令我到底惦着江南了。－－这样想着，猛一抬头，不觉已是自己的门前；轻轻地推门进去， <br style=\"content: &quot;&quot;; display: block; width: 700px; height: 0px; margin: 20px 0px;\" />什么声息也没有，妻已睡熟好久了。</pre>',2,'2017-04-16 16:30:34','2017-04-16 16:30:34',1,0,0),(23,'创青春2','<pre id=\"best-content-1509217389\" class=\"best-text mb-10\" style=\"marg